!(() => {
  "use strict"


  // ----------------------------------------------------------------------
  // Number Extensions
  // ----------------------------------------------------------------------

  Number.prototype.toRad = function () {
    return this * Math.PI / 180
  }

  Number.prototype.toDeg = function () {
    return this * 180 / Math.PI
  }

})()
